/**
* Функция конструктор для создания нового пользователя.
*
* @param  {String} name Имя/логин пользователя.
* @param  {String} password Пароль.
* @return  {Object} Объект {key: data}.
*/
var User = function(name, password) {
    this.name = name;
    this.password = password;
    this.categories = {
        accounts: ["Кошелек", "Банк"],
        costs: ["Продукты", "Авто", "Дети", "Одежда", "Подарки"],
        gain: ["Зарплата"]
    };
    this.mainCurr = 'uah';
    this.history = {};
    this.currency = {
        uah: {
            rub: 0,
            usd: 0,
            eur: 0
		},
		rub: {
            uah: 0,
            usd: 0,
            eur: 0
		},
		usd: {
            uah: 0,
            rub: 0,
            eur: 0
		},
		eur: {
            uah: 0,
            rub: 0,
            usd: 0
        }
    };
};

module.exports = User;

