    function changeCurr(user, curr, res) {
        db.open(function(err, db) {
            if (!err) {
                db.collection('users', function(err, collection) {
                    if (!err) {
                        collection.update({name: user},
                                          {$set: {mainCurr: curr}},
                                          function(err, result) {
                            if (!err) {
                                allUsers[user].changeMainCurr(curr);
                                db.close();
                                res.end(); //TODO в случае ошибки вывести леер и вернуть инпут в обратное положение чекед
                            }
                        });
                    }
                });
            }
        });
    }

    function setMainCurr(user, valuta, res) {
        //todo изменение валюты?
    }

    function recalculate (user) {
        var mainCurr = allUsers[user].mainCurr,
            mainVal = allUsers.currency[mainCurr].price;
        for (var key in mainVal) {
            var elem = allUsers.currency[key].price;
            var valuta = 1 / mainVal[key];
            elem[mainCurr] = valuta;
                for (var j in elem) {
                    if (j !== mainCurr) {
                        elem[j] = valuta * mainVal[j];
                    }
                }
        }
    }


    function extendDeep(parent, child) { // TODO возможно стоит пересмотреть наследование
        var i,
            toStr = Object.prototype.toString,
            astr = '[object Array]';
        child = child || {};
        for (i in parent) {
            if (parent.hasOwnProperty(i)) {
                if (typeof parent[i] === 'object') {
                    child[i] = (toStr.call(parent[i]) === astr) ? [] : {};
                    extendDeep(parent[i], child[i]);
                } else {
                    child[i] = parent[i];
                }
            }
        }
        return child;
    }

	return {
		reg: reg,
        auth: auth,
        changeCurr: changeCurr,
        newCat: newCat
	}
